import types from './contacts-types'
import { v4 as uuidv4 } from 'uuid'
import { createAction } from '@reduxjs/toolkit'

const addContact = createAction(types.ADD, (name, number) => ({
    payload: {
        id: uuidv4(),
        name,
        number,
    }
}));
// const addContact = ({name, number}) => ({
//     type: types.ADD,
//     payload: {
//         id: uuidv4(),
//         name,
//         number,
//     }
// })

const removeContact = createAction(types.REMOVE);
// const removeContact = contactId => ({
//     type: types.REMOVE,
//     payload: contactId,
// })

const changeFilter = createAction(types.CHENGE_FILTER);
// const changeFilter = value => ({
//     type: types.CHENGE_FILTER,
//     payload: value,
//   })

export default { addContact, removeContact, changeFilter }
