import React, { Component } from 'react'
import PropTypes from 'prop-types'

export default class Filter extends Component {
    static propTypes = {
        prop: PropTypes
    }
    changeFilter=e=>{
        this.setState({filter: e.currentTarget.value})
    }

    render() {
        return (
            <div>
                <label>
                    <input 
                        type="text"
                        name="filter"
                        // value={this.state.filter}   
                        // onChange={} 
                    />                    
                </label>
            </div>
        )
    }
}
