import { combineReducers, createStore } from 'redux';
import { composeWithDevTools } from 'redux-devtools-extension';

const rootReducer = combineReducers({
    contacts: [],
    filter: '',
    showNoty: false,
})
const initialState = {
    contacts: {
        name: '',
        number: '',
    }, 
    showNoty: false,
    
}

const reducer = (state = initialState, {type,payload}) => {
    switch(type) {
        case 'contacts/Add' :
            return {
                ...state,

                contacts: {
                    ...state.constacts,
                        name: state.contacts.name + payload,
            }
        }
            // console.log(state);
