            //             setTimeout(() => this.setState({showNoty: false}), 3000)      
            //     } else {
            //         this.initialState.setState(prevState=>({
            //             contacts: [payload, ...prevState.contacts],
            //         }))
            //     }
            // }
        
 
                // this.setState(prevState=>{
                //     return{
                //         contacts: prevState.contacts.filter(({id})=>id !== payload),
                //         filter: '',
                //     }
                // })  
            
        case 'contacts/Remove' :
            return {
                contacts: state.constacts - payload,
            }
            // console.log('remove');
            
        default : return state;    
    }
}

const store = createStore(reducer, composeWithDevTools());

export default store
