import { createStore } from 'redux';

const initialState = {
    contacts: [], 
    showNoty: false,
    name: '',
    number: '',
}

const reducer = (state = initialState, action) => {
    switch(action.type) {
        case 'contacts/Add' :
            return (
                // if(state.contacts.find((item)=>item.name===name)) {
                //     this.setState({showNoty: true})
                //         setTimeout(() => this.setState({showNoty: false}), 3000)      
                // } else {
                initialState.setState(prevState=>({
                    contacts: [action.payload, ...prevState.contacts],
                }))
            )
        
        case 'contacts/Remove' :
            return (
                this.setState(prevState=>{
                    return{
                        contacts: prevState.contacts.filter(({id})=>id !== action.payload),
                        filter: '',
                    }
                })  
            )
            
        default : return state;    
    }
}

const store = createStore(reducer);

export default store
