import React, { Component } from 'react';
import { v4 as uuidv4 } from 'uuid';
import styles from './ContactForm.module.css';
import { connect } from 'react-redux';
import Notification from '../Notification/Notification'
import contactsActions from '../../redux/contacts-actions'


class ContactForm extends Component {
    state = {
        name: '',
        number: '',
        // message: null,
    }

    // nameInputId = uuidv4();
    // numberInputId = uuidv4();

    handleInputChange=evt=>{
      const {name, value}= evt.currentTarget;
      this.setState({
        [name]: value,
      })
    }

    // getNotyfication = (note) =>{
    //   this.setState({ message: note});
    //   setTimeout(() => {
    //     this.setState ({ message: null});
    //   }, 3000);
    // }

    handleSubmit=evt=>{
      // const { name, number } = this.state;
        evt.preventDefault();

        // if (name === '') {
        //   this.getNotyfication('Enter name!');
        //   return;
        // }

        // if (number === '') {
        //   this.getNotyfication('Enter number!');
        //   return;
        // }

        // if (this.props.contacts.find((item) => item.name.toLowerCase() === name.toLowerCase())) {
        //   this.getNotyfication('Contact already exist!');
        //   return;
        // }

        console.log(this.state);
        // this.props.onSave();
        this.props.onSubmit(this.state.name);
        this.setState({ message:'' });
    }

    // reset=()=>{
    //     this.setState({name: '', number: '',});
    // }
    
    render() {
      const { name, number } = this.state;
        return (
            <div>
              {/* <Notification 
                message={message} /> */}
                <form 
                  onSubmit={this.handleSubmit}
                  className={styles.form}>
                  <label 
                    className={styles.label}
                    htmlFor={this.nameInputId}>
                        Name
                    <input 
                      className={styles.input}
                      type="text"
                      name="name"
                      value={this.state.name}
                      onChange={this.handleInputChange}
                      id={this.nameInputId}
                    />
                  </label>
                  <label 
                  className={styles.label}
                  htmlFor={this.numberInputId}>
                      Number
                    <input 
                      className={styles.input}
                      type="tel"
                      name="number"
                      value={this.state.number}
                      onChange={this.handleInputChange}
                      id={this.numberInputId}
                    />
                  </label>
                  <button 
                    className={styles.button}
                    type="submit" 
                    // onClick={this.handleSubmit}
                  >
                    Add contact
                  </button>
                </form>
            </div>
        )
    }
}

// const mapStateToProps = (state) => ({
//   contacts: state.contacts,
// })

const mapDispatchToProps = dispatch => ({
  onSubmit: (name, number) => dispatch(contactsActions.addContact(name,number)),
})


export default connect(null,mapDispatchToProps)(ContactForm)
