import React, { Component } from 'react';
import { v4 as uuidv4 } from 'uuid';
import styles from './ContactForm.module.css';
import {connect} from 'react-redux'
import contactsActions from '../../redux/contacts-actions'
import Notification from '../Notification/Notification'

class ContactForm extends Component {
    state = {
        // name: '',
        // number: '',
        message: null,
    }

    nameInputId = uuidv4();
    numberInputId = uuidv4();

    handleInputChange=evt=>{
      const {name, value}= evt.currentTarget;
      this.setState({
        [name]: value,
      })
    }

    getNotification = (notification) =>{
      this.setState ({message: notification});
      setTimeout(() => {
        this.setState({message: null});
      }, 3000)
    }

    handleSubmit=evt=>{
      const { name, number } = this.state;
        evt.preventDefault();
        // if (this.props.contacts.items.find((item)=>item.name===this.state.name)) {
        //       this.setState({showNoty: true})
        //         setTimeout(() => this.setState({showNoty: false}), 3000)      
        //     } 
        // if (name === '') {
        //   this.getNotification('Enter the name!');
        //   return;
        // }

        // if (number === '') {
        //   this.getNotification('Enter the number!');
        //   return;
        // }

        // if (this.props.contacts.find((item)=>item.name.toLowerCase() === name.toLowerCase())) {
        //   this.getNotification('Contact already exist!');
        //   return;
        // }
        console.log(this.state);
        this.props.onSubmit(this.state.message);
        this.reset()
    }

    reset=()=>{
        this.setState({name: '', number: '',});
    }
    
    render() {
      const { name, number, message } = this.state;
        return (
            <div>
              <Notification message={message}/>
                <form 
                  onSubmit={this.handleSubmit}
                  className={styles.form}>
                  <label 
                    className={styles.label}
                    htmlFor={this.nameInputId}>
                        Name
                    <input 
                      className={styles.input}
                      type="text"
                      name="name"
                      value={name}
                      onChange={this.handleInputChange}
                      id={this.nameInputId}
                    />
                  </label>
                  <label 
                  className={styles.label}
                  htmlFor={this.numberInputId}>
                      Number
                    <input 
                      className={styles.input}
                      type="tel"
                      name="number"
                      value={number}
                      onChange={this.handleInputChange}
                      id={this.numberInputId}
                    />
                  </label>
                  <button 
                    className={styles.button}
                    type="submit" 
                    onClick={this.handleSubmit}
                  >
                    Add contact
                  </button>
                </form>
            </div>
        )
    }
}

const mapDispatchToProps = dispatch =>({
  onAddContact: ({name, number}) => dispatch(contactsActions.addContact({name,number}))
})

export default connect(null,mapDispatchToProps)(ContactForm)
