import React, { Component } from 'react';
// import { v4 as uuidv4 } from 'uuid';
import styles from './ContactForm.module.css';
import { connect } from 'react-redux';
import contactsActions from '../../redux/contacts-actions'

import Notification from '../Notification/Notification'
import { CSSTransition } from 'react-transition-group'



class ContactForm extends Component {
    state = {
          name: '',
          number: '',   
    }

    // nameInputId = uuidv4();
    // numberInputId = uuidv4();

    handleInputChange=evt=>{
      const {name, value}= evt.currentTarget;
      this.setState({
        [name]: value,
      })
    }


    getNoty = (note) => {
      this.setState({message: note});
      setTimeout(()=> {
        this.setState({ message: null});
      }, 2500)
    }

    handleSubmit=evt=>{
        evt.preventDefault();
        console.log(this.state);

        if(this.props.item.find((item) => item.name.toLowerCase() === name.toLowerCase())) {
          this.getNoty('already exust');
        return;
        }

        this.props.onSubmit(this.state);
        this.reset()
    }

    reset=()=>{
        this.setState({name: '', number: '',});
    }
    
    render() {
      const { message }= this.state;
        return (
            <div>

              <CSSTransition
          in={message} 
          timeout={250}
          classNames="notification"
          unmountOnExit>
            <Notification />
        </CSSTransition>
                
                <form 
                  onSubmit={this.handleSubmit}
                  className={styles.form}>
                  <label 
                    className={styles.label}
                    htmlFor={this.nameInputId}>
                        Name
                    <input 
                      className={styles.input}
                      type="text"
                      name="name"
                      value={this.state.name}
                      onChange={this.handleInputChange}
                      id={this.nameInputId}
                    />
                  </label>
                  <label 
                  className={styles.label}
                  htmlFor={this.numberInputId}>
                      Number
                    <input 
                      className={styles.input}
                      type="tel"
                      name="number"
                      value={this.state.number}
                      onChange={this.handleInputChange}
                      id={this.numberInputId}
                    />
                  </label>
                  <button 
                    className={styles.button}
                    type="submit" 
                    onClick={this.handleSubmit}
                  >
                    Add contact
                  </button>
                </form>
            </div>
        )
    }
}

const mapStateToProps = (state) => ({
  contacts: state.contacts.item,
})

const mapDispatchToProps = dispatch => ({
  onSubmit: ({name,number}) => dispatch(contactsActions.addContact(name,number))
})

export default connect(mapStateToProps, mapDispatchToProps)(ContactForm)
