import types from './contacts-types'
import { v4 as uuidv4 } from 'uuid'

const addContact = (name,number) => ({
    type: types.ADD,
    payload: {
        id: uuidv4(),
        name,
        number,
    }
})

const removeContact = contactId => ({
    type: types.REMOVE,
    payload: contactId,
})

export default { addContact, removeContact }
