import { v4 as uuidv4 } from 'uuid'
import { createAction } from '@reduxjs/toolkit'


const addContact = (name,number,message) => dispatch => {
    const contact = {
        id: uuidv4(),
        name,
        number,
        message
    };

    dispatch({ type: 'contacts/addContactRequest' })

    axios
        
        .post('/contacts', contact)
        .then(({ data }) => 
            dispatch({ type: 'contacts/addContactSuccess', payload: data }),
        )
        .catch(error => dispatch({ type: 'contacts/addContactError', payload: error}))
    }

// const addContact = createAction('contacts/Add', (name,number,message) => ({
//     payload: {
//         id: uuidv4(),
//         name,
//         number,
//         message
//     }
// }));

const removeContact = createAction('contacts/Remove')

const changeFilter = createAction('contacts/ChangeFilter');

export default { addContact, removeContact, changeFilter }
