import React, { Component } from 'react';
import { v4 as uuidv4 } from 'uuid';
import styles from './ContactForm.module.css';
import { connect } from 'react-redux';
import Notification from './components/Notification/Notification'


class ContactForm extends Component {
    state = {
        name: '',
        number: '',
        message: null,
    }

    nameInputId = uuidv4();
    numberInputId = uuidv4();

    handleInputChange=evt=>{
      const {name, value}= evt.target;
      this.setState({
        [name]: value,
      })
    }

    getNotyfication = (note) =>{
      this.setState({ message: note});
      setTimeout(() => {
        this.setState ({ message: null});
      }, 3000);
    }

    

    handleSubmit=evt=>{
      const { name, number } = this.state;
        evt.preventDefault();

        if (name === '') {
          this.getNotyfication('Enter name!');
          return;
        }

        if (number === '') {
          this.getNotyfication('Enter number!');
          return;
        }

        if (this.props.contacts.find((item) => item.name.toLowerCase() === name.toLowerCase())) {
          this.getNotyfication('Contact already exist!');
          return;
        }

        console.log(this.state);
        this.props.onSubmit(name, number);
        this.setState({
          name:'',
          number:'',
        })
    }

    // reset=()=>{
    //     this.setState({name: '', number: '',});
    // }
    
    render() {
      const { name, number, message } = this.state;
        return (
            <div>
              <Notification 
                message={message} />
                <form 
                  onSubmit={this.handleSubmit}
                  className={styles.form}>
                  <label 
                    className={styles.label}
                    htmlFor={this.nameInputId}>
                        Name
                    <input 
                      className={styles.input}
                      type="text"
                      name="name"
                      value={this.state.name}
                      onChange={this.handleInputChange}
                      id={this.nameInputId}
                    />
                  </label>
                  <label 
                  className={styles.label}
                  htmlFor={this.numberInputId}>
                      Number
                    <input 
                      className={styles.input}
                      type="tel"
                      name="number"
                      value={this.state.number}
                      onChange={this.handleInputChange}
                      id={this.numberInputId}
                    />
                  </label>
                  <button 
                    className={styles.button}
                    type="submit" 
                    // onClick={this.handleSubmit}
                  >
                    Add contact
                  </button>
                </form>
            </div>
        )
    }
}

const mapStateToProps = (state) => ({
  contacts: state.contacts.contacts,
})

const mapDispatchToProps = dispatch => ({
  onSubmit: (name, number) => dispatch(contactsAction.addContact(name, number)),
})


export default connect(mapStateToProps, mapDispatchToProps)(ContactForm)
