import { createStore } from 'redux';

const initialState = {
    contacts: [], 
    showNoty: false,
    name: '',
    number: '',
}

const reducer = (state = initialState, {type,payload}) => {
    switch(type) {
        case 'contacts/Add' :
                if (state.contacts.find((item)=>item.name===payload.contact.name)) {
                    this.state.setState({showNoty: true})
                        setTimeout(() => this.setState({showNoty: false}), 3000)      
                } else {
                    this.state.setState(prevState=>({
                        contacts: [payload, ...prevState.contacts],
                    }))
                }
        
        case 'contacts/Remove' :
            return (
                this.setState(prevState=>{
                    return{
                        contacts: prevState.contacts.filter(({id})=>id !== payload),
                        filter: '',
                    }
                })  
            )
            
        default : return state;    
    }
}

const store = createStore(reducer);

export default store
