import axios from 'axios'
import { v4 as uuidv4 } from 'uuid'
import { addContactRequest, addContactSuccess, addContactError, removeContactRequest, removeContactSuccess, removeContactError } from './contacts-actions'

axios.defaults.daseURL = 'http://localhost:3000';

const addContact = (name,number,message) => dispatch => {
    const contact = {
        id: uuidv4(),
        name,
        number,
        message
    };

    dispatch(addContactRequest())

    axios
        .post('/contacts', contact)
        .then(({ data }) => 
            dispatch(addContactSuccess(data)))
        .catch(error => dispatch(addContactError(error)))
    }


const removeContact = contactId => dispatch => {
    dipatch(removeContactRequest());

    axios
        .delete(`/contacts/${contactId}`)
        .then(() => dispatch(removeContactSuccess(contactId)))
        .catch(error => dispatch(removeContactError(error)))
}
    export default {
        addContact,
        removeContact
    }
