    name: ''
  }
  handleNameChange=evt=>{
    console.log(evt.currentTarget.value);
  }

  render() {
    return(
      <div>
        <form>
          <label>
            <input 
              type="text"
              value={this.state.name}
              onChange={this.handleNameChange}
            />
