import React from 'react';
import ContactsItem from '../ContactsItem/ContactsItem';
import PropTypes from 'prop-types'
import { CSSTransition, TransitionGroup } from 'react-transition-group'
import styles from './Contacts.module.css'
import { connect } from 'react-redux';
import contactsActions from '../../redux/contacts-actions'

// const handleRemoveContact = evt => ();

const Contacts = ({contacts,onRemoveContact,clearFilterOnRemove}) => {
    return(
        <TransitionGroup 
            component="ul" 
            className="ContactsList"
            >
                <h2>Contacts</h2>
                {contacts.map(({id,name,number})=>(
                    <CSSTransition 
                        key={id}
                        timeout={2500}
                        classNames={styles}
                        >
                        <ContactsItem 
                            id={id}
                            name={name}
                            number={number}
                            onRemove={()=>onRemoveContact(id)}
                        />
                    </CSSTransition>
                    
                ))}
        </TransitionGroup>                       
    )
}
Contacts.propTypes={
    id:PropTypes.string,
    name:PropTypes.string,
    number:PropTypes.string,
}

const getVisibleContacts=(allContacts, filter) => {
  const normalizedFilter = filter.toLowerCase();

  return allContacts.filter(({name})=>
    name.toLowerCase().includes(normalizedFilter),
  )
}

const mapStateToProps =({ contacts: { items, filter } })=> ({
    contacts: getVisibleContacts(items, filter),
})

const mapDispatchToProps = dispatch => ({
    onRemoveContact: (id) => dispatch(contactsActions.removeContact(id),dispatch(contactsActions.changeFilter('')))
    // clearFilterOnRemove: () => dispatch(contactsActions.changeFilter(''))
})

export default connect(mapStateToProps, mapDispatchToProps)(Contacts)
