import types from './contacts-types'
import { v4 as uuidv4 } from 'uuid'
import { createAction } from '@reduxjs/toolkit'

const addContact = createAction(types.ADD, contact => {
    return {
        payload: {
            id: uuidv4(),
            name,
            number,
        }
    }
});

console.log(addContact('viki'));
